import React, { useState } from 'react';

function TokenForm() {
    const [token, setToken] = useState("");
    const [hasError, setHasError] = useState(false);

    const handleTokenChange = (e) => {
        setToken(e.target.value);
        setHasError(false); // Reset error state when user changes input
    };

    const handleSubmit = () => {
        // Example check: here we're just checking if token is '123456'
        if (token !== "123456") {
            setHasError(true);
        } else {
            // Proceed to next form or whatever functionality you want
            console.log("Correct token");
        }
    };


  return (
    <div class="center-container">
        <h2 class="token-prompt">Please enter your token to create or access your resources</h2>
        <input 
                type="text" 
                className={`token-input ${hasError ? "error" : ""}`} 
                placeholder="Enter your token"
                value={token}
                onChange={handleTokenChange}
            />        <p class="token-help">If you forgot your token, <a href="#" class="token-link">click here</a> to receive a new one.</p>
        <div class="button-container">
            <button className="submit" type="submit" onClick={handleSubmit}>Submit</button>
        </div>
    </div>



  );
}

export default TokenForm;
